["KLD32"]={{Date="2016-09-20";Text=[=[If you control no artifacts as your combat phase begins, Toolcraft Exemplar’s triggered ability doesn’t trigger at all. If you control no artifacts as the triggered ability resolves, it has no effect.]=];};
